action.script:
# paths
PWD=$(pwd)
filepath=${file}
filedir=$(dirname ${filepath})
fileext=$(echo "$filepath" | sed 's/^.*\.//')
filebase=$(basename $filepath .${fileext})
totalfilepath=${PWD}/${filedir}
BOMfilepath=${totalfilepath}/BOM

# Windows Style Path
DRIVE=${totalfilepath:1:1}
DIRA=${totalfilepath:2}
DIRB=${DIRA//\//\\}
ORGfilepathWIN="${DRIVE}:${DIRB}\\${file:name}"

if [ ${fileext} == "SLDASM" ] || [ ${fileext} == "sldasm" ]
then
    python /d/github/git-fork-sw/swBOM-CSV.py ${ORGfilepathWIN}
else
    echo "This is not .SLDASM file."
fi

echo ${BOMfilepath}/${filebase}.csv

# paths
PWD=$(pwd)
filepath=${file}
filedir=$(dirname ${filepath})
fileext=$(echo "$filepath" | sed 's/^.*\.//')
filebase=$(basename $filepath .${fileext})
totalfilepath=${PWD}/${filedir}

# Windows Style Path
DRIVE=${totalfilepath:1:1}
DIRA=${totalfilepath:2}
DIRB=${DIRA//\//\\}
ORGfilepathWIN="${DRIVE}:${DIRB}\\${file:name}"
PDFfilepathWIN="${DRIVE}:${DIRB}\\2D\\${filebase}.pdf"

if [ ${fileext} == "SLDDRW" ] || [ ${fileext} == "slddrw" ]
then
    OUTPUT=$(python /d/github/git-fork-sw/swSaveAs.py ${ORGfilepathWIN} pdf)
    mupdf ${OUTPUT}
    echo ${OUTPUT}
else
    echo "This is not .SLDDRW file."
fi

# paths
PWD=$(pwd)
filepath=${file}
filedir=$(dirname ${filepath})
fileext=$(echo "$filepath" | sed 's/^.*\.//')
filebase=$(basename $filepath .${fileext})
totalfilepath=${PWD}/${filedir}

# Windows Style Path
DRIVE=${totalfilepath:1:1}
DIRA=${totalfilepath:2}
DIRB=${DIRA//\//\\}
ORGfilepathWIN="${DRIVE}:${DIRB}\\${file:name}"

if [ ${fileext} == "IGES" ] || [ ${fileext} == "IGS" ] || [ ${fileext} == "STL" ] || [ ${fileext} == "DXF" ] || [ ${fileext} == "DWG" ] || [ ${fileext} == "STEP" ] || [ ${fileext} == "STP" ] || [ ${fileext} == "SLDPRT" ] || [ ${fileext} == "SLDASM" ] || [ ${fileext} == "SLDDRW" ] || [ ${fileext} == "iges" ] || [ ${fileext} == "igs" ] || [ ${fileext} == "stl" ] || [ ${fileext} == "dxf" ] || [ ${fileext} == "dwg" ] || [ ${fileext} == "step" ] || [ ${fileext} == "stp" ] || [ ${fileext} == "sldprt" ] || [ ${fileext} == "sldasm" ] || [ ${fileext} == "slddrw" ]
then
    eDrawings.exe ${ORGfilepathWIN}
else
    echo "This is not 2D,3D file."
fi
