    PWD=$(pwd)
    DRIVE=${PWD:1:1}
    DIRA=${PWD:2}
    DIRB=${DIRA//\//\\}
    filepathA="${DRIVE}:${DIRB}\\${filenameA}"
    TOTAL=${filepathA//\//\\}
